#include <iostream>
#include <chrono>
#include "utils/BigInteger.h"

int main(int argc, char * argv[]) {
    bool passed = true;
    bool result;
    auto restart = std::chrono::high_resolution_clock::now();
    
    std::cout << "BigInteger code test \n";
    BigInteger a(9), b(7);
    // ">" operator test
    result = (a > b) && !(b > a);
    passed &= result;
    if (!result)
        std::cout << "    '>' operator error \n";
    // "<" operator test
    result = (b < a) && !(a < b);
    passed &= result;
    if (!result)
        std::cout << "    '<' operator error \n";
    // ">=" operator test
    result = (a >= b) && !(b >= a);
    passed &= result;
    if (!result)
        std::cout << "    '>=' operator error \n";
    // "<=" operator test
    result = (b <= a) && !(a <= b);
    passed &= result;
    if (!result)
        std::cout << "    '<=' operator error \n";
    // "==" operator test
    result = (b == b) && !(b == a);
    passed &= result;
    if (!result)
        std::cout << "    '==' operator error \n";
    // "!=" operator test
    result = (b != a) && !(b != b);
    passed &= result;
    if (!result)
        std::cout << "    '!=' operator error \n";

    // arithmatics operation test
    // "+" operator test
    std::cout << "    '+' operator test \n";
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = true;
    result &= ((b + b) == a);
    result &= ((a + (-b)) == b);
    result &= (((-a) + b) == (-b));
    result &= (((-b) + (-b)) == (-a));
    passed &= result;
    if (!result)
        std::cout << "    '+' operator error \n";
    // "-" operator test
    result = true;
    result &= ((a - b) == b);
    result &= (((-a) - (-b)) == (-b));
    result &= (((-b) - b) == (-a));
    result &= ((b - (-b)) == a);
    passed &= result;
    if (!result)
        std::cout << "    '-' operator error \n";
    // "+=" operator test
    result = true;
    result &= ((b += b) == a);
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= ((a += (-b)) == b);
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= (((-a) += b) == (-b));
    a = BigInteger("-\0");
    b = BigInteger("-\0");
    result &= ((b += b) == a);
    passed &= result;
    if (!result)
        std::cout << "    '+=' operator error \n";
    // "-=" operator test
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = true;
    result &= ((a -= b) == b);
    a = BigInteger("-\0"),
        b = BigInteger("-\0");
    result &= ((a -= b) == b);
    a = BigInteger("-\0");
    b = BigInteger("\0");
    result &= (((-b) -= b) == a);
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= ((b -= (-b)) == a);
    passed &= result;
    if (!result)
        std::cout << "    '-=' operator error \n";
    return 0;
    // "*" operator test
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = true;
    result &= ((a * a) == b);
    result &= (((-a) * (-a)) == b);
    result &= ((a * (-a)) == (-b));
    result &= (((-a) * a) == (-b));
    passed &= result;
    if (!result)
        std::cout << "    '*' operator error \n";
    // "/" operator test
    result = true;
    result &= ((b / a) == a);
    result &= ((b / (-a)) == (-a));
    result &= (((-b) / (-a)) == a);
    result &= (((-b) / a) == (-a));
    if (!result)
        std::cout << "    '/' operator error \n";
    passed &= result;
    // "*=" operator test
    result = true;
    result &= ((a *= a) == b);
    a = BigInteger("-\0");
    b = BigInteger("\0");
    result &= ((a *= a) == b);
    a = BigInteger("\0");
    b = BigInteger("-\0");
    result &= ((a *= (-a)) == b);
    a = BigInteger("-\0");
    b = BigInteger("-\0");
    result &= ((a *= (-a)) == b);
    passed &= result;
    if (!result)
        std::cout << "    '*=' operator error \n";
    // "/=" operator test
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = true;
    result &= ((b /= a) == a);
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= ((b /= (-a)) == (-a));
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= (((-b) /= (-a)) == a);
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= (((-b) /= a) == (-a));
    passed &= result;
    if (!result)
        std::cout << "    '/=' operator error \n";
    // "%" operator test
    a = BigInteger("\0"),
        b = BigInteger("\0");
    result = ((b % a) == 4);
    passed &= result;
    if (!result)
        std::cout << "    '%' operator error \n";
    // "%=" operator test
    result = ((b %= a) == 4);
    passed &= result;
    if (!result)
        std::cout << "    '%=' operator error \n";
    // ">>" operator test
    a = BigInteger("1152921504606846976000000000000000000\0"), b = BigInteger("1000000000000000000\0");
    result = ((a >> 60) == b);
    passed &= result;
    if (!result)
        std::cout << "    '>>' operator error \n";
    // "<<" operator test
    result = ((b << 60) == a);
    passed &= result;
    if (!result)
        std::cout << "    '<<' operator error \n";
    // ">>=" operator test
    result = ((a >>= 60) == b);
    passed &= result;
    if (!result)
        std::cout << "    '>>=' operator error \n";
    a = BigInteger("1152921504606846976000000000000000000\0"), b = BigInteger("1000000000000000000\0");
    // "<<=" operator test
    result = ((b <<= 60) == a);
    passed &= result;
    if (!result)
        std::cout << "    '<<=' operator error \n";
    a = BigInteger("1152921504606846976\0"), b = 2;
    // "^" operator test
    result = ((b ^ 60) == a);
    passed &= result;
    if (!result)
        std::cout << "    '^' operator error \n";
    // "^=" operator test
    result = ((b ^= 60) == a);
    passed &= result;
    if (!result)
        std::cout << "    '^=' operator error \n";

    std::cout << "In : " << std::chrono::duration_cast < std::chrono::microseconds > (std::chrono::high_resolution_clock::now() - restart).count() << " us" << std::endl;
    if (!passed)
        std::cout << "Passed \n";
    return EXIT_SUCCESS;
}