#include "BigInteger.h"

#include <chrono>
#include <iostream>
#include <iomanip>

bool BigInteger_test() {
  bool passed = true;
  std::chrono::time_point<std::chrono::high_resolution_clock>restart = std::chrono::high_resolution_clock::now();
  std::cout << "BigInteger code test" << std::endl;
  try {
    bool result;
    BigInteger a = "171099382929\0", b = "79092001\0";
    // ">" operator test
    result = (a>b) && !(b>a);
    if (!result) throw ("    '>' operator error");
    // "<" operator test
    result = (b<a) && !(a<b);
    if (!result) throw ("    '<' operator error");
    // ">=" operator test
    result = (a >= b) && !(b >= a);
    if (!result) throw ("    '>=' operator error");
    // "<=" operator test
    result = (b <= a) && !(a <= b);
    if (!result) throw ("    '<=' operator error");
    // "==" operator test
    result = (b == b) && !(b == a);
    if (!result) throw ("    '==' operator error");
    // "!=" operator test
    result = (b != a) && !(b != b);
    if (!result) throw ("    '!=' operator error");
    // arithmatics operation test
    // "+" operator test
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = true;
    result &= ((b + b) == a);
    result &= ((a + (-b)) == b);
    result &= (((-a) + b) == (-b));
    result &= (((-b) + (-b)) == (-a));
    if (!result) throw ("    '+' operator error");
    // "-" operator test
    result = true;
    result &= ((a - b) == b);
    result &= (((-a) - (-b)) == (-b));
    result &= (((-b) - b) == (-a));
    result &= ((b - (-b)) == a);
    if (!result) throw ("    '-' operator error");
    // "+=" operator test
    result = true;
    result &= ((b += b) == a);
    if (!result) throw ("    '+=' operator error");
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= ((a += (-b)) == b);
    if (!result) throw ("    '+=' operator error");
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= (((-a) += b) == (-b));
    if (!result) throw ("    '+=' operator error");
    a = BigInteger("-\0");
    b = BigInteger("-\0");
    result &= ((b += b) == a);
    if (!result) throw ("    '+=' operator error");
    // "-=" operator test
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = true;
    result &= ((a -= b) == b);
    a = BigInteger("-\0"), b = BigInteger("-\0");
    result &= ((a -= b) == b);
    a = BigInteger("-\0");
    b = BigInteger("\0");
    result &= (((-b) -= b) == a);
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= ((b -= (-b)) == a);
    if (!result) throw ("    '-=' operator error");
    // "*" operator test
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = true;
    result &= ((a * a) == b);
    result &= (((-a) * (-a)) == b);
    result &= ((a * (-a)) == (-b));
    result &= (((-a) * a) == (-b));
    if (!result) throw ("    '*' operator error");
    // "/" operator test
    result = true;
    result &= ((b / a) == a);
    result &= ((b / (-a)) == (-a));
    result &= (((-b) / (-a)) == a);
    result &= (((-b) / a) == (-a));
    if (!result) throw ("    '/' operator error");
    // "*=" operator test
    result = true;
    result &= ((a *= a) == b);
    a = BigInteger("-\0");
    b = BigInteger("\0");
    result &= ((a *= a) == b);
    a = BigInteger("\0");
    b = BigInteger("-\0");
    result &= ((a *= (-a)) == b);
    a = BigInteger("-\0");
    b = BigInteger("-\0");
    result &= ((a *= (-a)) == b);
    if (!result) throw ("    '*=' operator error");
    // "/=" operator test
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = true;
    result &= ((b /= a) == a);
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= ((b /= (-a)) == (-a));
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= (((-b) /= (-a)) == a);
    a = BigInteger("\0");
    b = BigInteger("\0");
    result &= (((-b) /= a) == (-a));
    if (!result) throw ("    '/=' operator error");
    // "%" operator test
    a = BigInteger("\0"), b = BigInteger("\0");
    result = ((b % a) == 4);
    if (!result) throw ("    '%' operator error");
    // "%=" operator test
    result = ((b %= a) == 4);
    if (!result) throw ("    '%=' operator error");
    // ">>" operator test
    a = BigInteger("1152921504606846976000000000000000000\0"), b = BigInteger("1000000000000000000\0");
    result = ((a >> 60) == b);
    if (!result) throw ("    '>>' operator error");
    // "<<" operator test
    result = ((b << 60) == a);
    if (!result) throw ("    '<<' operator error");
    // ">>=" operator test
    result = ((a >>= 60) == b);
    if (!result) throw ("    '>>=' operator error");
    a = BigInteger("1152921504606846976000000000000000000\0"), b = BigInteger("1000000000000000000\0");
    // "<<=" operator test
    result = ((b <<= 60) == a);
    if (!result) throw ("    '<<=' operator error");
    a = BigInteger("1152921504606846976\0"), b = 2;
    // "^" operator test
    result = ((b ^ 60) == a);
    if (!result) throw ("    '^' operator error");
    // "^=" operator test
    result = ((b ^= 60) == a);
    if (!result) throw ("    '^=' operator error");
    // sqrt function test
    a = BigInteger("\0");
    b = BigInteger("\0");
    result = (b.sqrt() == a);
    if (!result) throw ("    sqrt function error");
  } catch (const char * msg) {
    std::cout << "Error has occure " << msg << std::endl;
    passed = false;
  }
  std::cout << "In : " << std::chrono::duration_cast<std::chrono::microseconds>(std::chrono::high_resolution_clock::now() - restart).count() << " us" << std::endl;
  std::cout << (passed ? "has " : " hasn\'t ") << " Passed" << std::endl;

  if (passed) {
    std::cout << "pi generator";
    std::chrono::time_point<std::chrono::high_resolution_clock>start = std::chrono::high_resolution_clock::now();
    BigInteger q = 1, r = 6, t = 3, k = 2, l = 5, n = 3;
    int N;
    unsigned long generated = 0;
    while (generated<60000) { //limit digits with 60.000
      if (q.tot()>20000) { //limit vector size 20.000
        std::cout << " max out";
        break;
      }
      if (!n.can_convert_to_int( & N)) {
        std::cout << " n value are broken and generate ";
        passed = false;
        break;
      }
      if ((q * 4 + r - t)<(t * n)) {
        generated++;
        q *= 10;
        r = (r - t * n) * 10;
        n = (q * 3 + r) / t;
      } else {
        t *= l;
        n = (q * (k * 7 + 2) + r * l) / t;
        r = (q * 2 + r) * l;
        q *= k;
        ++k;
        l += 2;
      }
    }
	std::cout << std::endl;
	std::cout << "pi generator gain : " << std::setfill('0') << std::setw(20) << generated << " digits" << std::endl;
    std::cout << "Time " << std::chrono::duration_cast<std::chrono::microseconds>(std::chrono::high_resolution_clock::now() - start).count() << " us" << std::endl;
  }
  return passed;
}